clear#PC1 - p3X = [1;5;9;10];Y = [2;9;10;8];Z = [3;7;11;7];W = [5;8;12;6];  #Ax = BA = [X Y Z W];  B = [100;200;300;400];  #Usando CramerSx = det([B Y Z W])/det(A);Sy = det([X B Z W])/det(A);Sz = det([X Y B W])/det(A);Sw = det([X Y Z B])/det(A);valores = [Sx,Sy,Sz,Sw];for i=1:4  format short  fprintf("x =%12.4f\t",valores(i));  format rat  fprintf("=    "),disp(valores(i))endfor